    @Test
    public void testDecodePacket() throws IOException
    {
        NRPEPacket packet = NRPEPacket.parse(test_buffer, 0, test_buffer.length);
        assertTrue("Packet is version 2", packet.getVersion() == PacketVersion.NRPE_2);
        assertTrue("Packet type is query", packet.getType() == PacketType.QUERY);
        assertTrue("Command is \"check_disk_root\"", "check_disk_root".equals(packet.getMessage()));
    }
    
    @Test
    public void testEncodeDecode() throws IOException
    {
        NRPEPacket packet1 = new NRPEPacket().version2().query().hello();
        NRPEPacket packet2 = NRPEPacket.parse(packet1.encodePacket());
        //
        assertTrue("Packet is version 2", packet2.getVersion() == PacketVersion.NRPE_2);
        assertTrue("Packet type is query", packet2.getType() == PacketType.QUERY);
        assertTrue("Command is \"_NRPE_CHECK\"", "_NRPE_CHECK".equals(packet2.getMessage()));
    }
