div.script:
$(document).ready(function() {
	var poll = function() {
	   	$.getJSON('/admin/configchange/poll/apply/id/' + #{'\'' + taskid + '\''}, function(data) { 
			if (data.stat == 'ok')
			{
				if (data.complete)
				{
					$('#loader').css('display', 'none');
					$('#report').text(data.result);
				}
				else
				{
					setTimeout(poll, 2000);
				}	
			}
			else
			{
				$('#loader').css('display', 'none');
				$('#report').text('Error: ' + data.message);
			}
		});
	};
	setTimeout(poll, 2000);
})
