fragment.script:
(function() {

	function update_grants() {
		$('#timeranges input[type=text]').each(function(i, e) {
			if ($(e).attr('id') != 'available_grants') {
				$(e).attr('id', 'timerange_' + i);
				$(e).attr('name', 'timerange[' + i + ']');
			}
		});
	}

	function remove_grant(ev) {
		ev.preventDefault();
		$(this).parent().remove();
		update_grants();
	}

	function add_grant(ev) {
		ev.preventDefault();
		// input
		var sel = $('#available_grants').clone();
		$(sel).attr('id', '');
		$(sel).attr('name', '');
		$(sel).attr('style', '');
		// remove
		var rm  = document.createElement('a');
		$(rm).attr('href', '#remove');
		$(rm).text('Remove');
		$(rm).attr('class', 'remove');
		// containers
		var li = document.createElement('li');
		$(li).html([sel, rm]);
		// insert
		$('#add_grant_row').before(li);
		// actions
		$(rm).click(remove_grant);
		// update
		update_grants();
	}

	// setup
	$('#add_grant').click(add_grant);
	$('#grants .remove').click(remove_grant);
})();

