div.script:
    			$(document).ready(function() {
    				$('#u2f-authenticate').submit(function(ev) {
    				    if ($('#u2f-authenticate').attr('data-state') != 'go')
    				    {
	    				    ev.preventDefault();
	    				    var req = JSON.parse($('#u2f-authenticate-request').val());
	    				    /*alert('Please insert your U2F device');*/
	    				    u2f.sign(req.authenticateRequests,
						    function(data) {
						        if(data.errorCode)
						        {
						            alert("U2F failed with error: " + data.errorCode);
						            return;
						        }
						        $('#u2f-authenticate-response').val(JSON.stringify(data));
						        $('#u2f-authenticate').attr('data-state', 'go');
						        $('#u2f-authenticate').submit();
						    });
					    }
    				});
    			});
